mapper.select:
SELECT 

FROM tb_task a

WHERE a.id = #{id}

SELECT 

FROM tb_task a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.task_state = #{taskState}


		AND a.start_mode = #{startMode}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM tb_task a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT
t1.ID as "task_id" , t1.task_name as "task_name", t6.website_url as "task_url", t1.task_parse AS "task_file", t1.task_cron as "task_stime", t1.task_state as "task_staus", t6.website_type AS "task_type", t6.website_token AS "poi_key", DATE_FORMAT(t1.task_begin,'%Y-%c-%d %h:%i:%s') as "task_start_time", t1.retry_times as "retry_times", t7.database_url as "task_bind_db.db_url", t7.database_name as "task_bind_db.db_username", t7.login_psw as "task_bind_db.db_password", t5.table_code as "task_bind_db.tb_name", t3.service_name AS "service_name", t5.id as "table_id"
FROM tb_task t1 INNER JOIN tb_service_task t2 ON t2.task_ID=t1.ID and t2.del_flag='0' INNER JOIN tb_service t3 on t3.ID=t2.service_ID and t3.del_flag='0' INNER JOIN tb_collect_data t4 on t4.ID=t1.collect_data_id and t4.del_flag='0' INNER JOIN tb_collect_table t5 on t5.id=t4.collect_table_ID and t5.del_flag='0' INNER JOIN tb_database t7 ON t7.ID=t5.database_id and t7.del_flag='0' INNER JOIN tb_website t6 on t6.ID=t4.website_ID and t6.del_flag='0' WHERE t1.ID=#{id}

mapper.update:
UPDATE tb_task SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
