mapper.select:
SELECT 

FROM tb_service_err a

WHERE a.id = #{id}

SELECT 

FROM tb_service_err a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.task_id = #{taskId}


		AND a.err_type = #{errType}


		AND a.err LIKE 
			'%'||#{err}||'%'
			'%'+#{err}+'%'
			concat('%',#{err},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM tb_service_err a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE tb_service_err SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
