mapper.select:
SELECT 

FROM tb_service_task a

WHERE a.id = #{id}

SELECT 

FROM tb_service_task a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.service_id = #{serviceId}


		AND a.task_id = #{taskId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM tb_service_task a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE tb_service_task SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
