mapper.select:
SELECT 

FROM tb_website a

WHERE a.id = #{id}

SELECT
FROM tb_website a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.website_name LIKE '%'||#{websiteName}||'%' '%'+#{websiteName}+'%' concat('%',#{websiteName},'%')
AND a.website_url LIKE '%'||#{websiteUrl}||'%' '%'+#{websiteUrl}+'%' concat('%',#{websiteUrl},'%')
AND a.website_type = #{websiteType}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM tb_website a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE tb_website SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
